// CORE function: Create the Image with order_id_n
function create_image($order_id_n = '') {
    $conn = new mysqli(SERVERNAME, USERNAME, PASSWORD, DBNAME);
    if ($conn->connect_error) {
        return json_encode(['status' => 400, 'msg' => "DB Connection Error!"]);
    }
    $sql = "SELECT * FROM orders where order_id_n = '{$order_id_n}'";
    $result = $conn->query($sql);
    if ($result->num_rows == 0) {
        return json_encode(['status' => 400, 'msg' => "Not Found from DB!"]);
    }
    $row = $result->fetch_assoc();

    $order_id = $row["order_id"];
    $text_music = $row["titre_musique"];
    $text_personal = $row["phrase_personnalisee"];
    $cover_image = $row["cover_image"];
    $spotify_code = 'https://scannables.scdn.co/uri/plain/png/ffffff/black/2000/' . $row["spotify_code"];
    $datep = $row["datep"];
    $username = $row["firstname"] . '_' . $row["lastname"];
    $quantity =  $row["quantity"];
    $taille = $row["taille"];
    $taille = str_replace("cm", "", $taille);
    $taille = str_replace(' ', '', $taille);
    $taille = str_replace(':', '_', $taille);
    $ndate = date('dmY', strtotime($datep));
    $image_name = $order_id_n . '_' . $quantity . '_' . $taille . '_' . $username . '_' . $ndate;
    
    $fontsize1 = 80;
    $fontsize2 = 67;
    $fontsize3 = 39;
    $color = 'black';
    
    $image = new Imagick(realpath('./images/template2000.png'));
    
    $overlay_cover = new Imagick();
    $overlay_cover->readImageBlob(file_get_contents($cover_image));
    $overlay_cover->resizeImage(1796, 1796, Imagick::FILTER_LANCZOS, 1);
    $image->compositeImage($overlay_cover, Imagick::COMPOSITE_DEFAULT, 102, 47);
    
    $overlay_spot = new Imagick();
    $overlay_spot->readImageBlob(file_get_contents($spotify_code));
    $overlay_spot->floodFillPaintImage('#FF000000', 10, '#FFFFFF', 0, 0, false);
    $overlay_spot->floodFillPaintImage('#FF000000', 10, '#FFFFFF', 240, 194, false);
    $overlay_spot->floodFillPaintImage('#FF000000', 10, '#FFFFFF', 240, 242, false);
    $overlay_spot->floodFillPaintImage('#FF000000', 10, '#FFFFFF', 240, 290, false);
    $overlay_spot->resizeImage(1436, 359, Imagick::FILTER_LANCZOS, 1);
    $image->compositeImage($overlay_spot, Imagick::COMPOSITE_DEFAULT, 282, 1870);
    
    $draw = new ImagickDraw();
    $draw->setTextEncoding('UTF-8');
    $draw->setFontSize($fontsize1);
    $draw->setFillColor($color);
    $draw->setStrokeAntialias(true);
    $draw->setTextAntialias(true);
    
    $text_personal = process_text($text_personal);
    
    $arr = [];
    preg_match_all(EMOJIREG, $text_personal, $arr);
    
    $txt_pos = 96;
    foreach ($arr[0] as $value) {
        if (is_emoji($value) != false) {
            $draw->setFont(realpath("./images/fonts/seguiemj.ttf"));
        } else {
            $draw->setFont(realpath("./images/fonts/arialunicodems.ttf"));
        }
        $met = $image->queryFontMetrics($draw, $value);
        $draw->annotation($txt_pos, 2312, $value);
        $txt_pos += $met['textWidth'];
    }
    
    $draw->setFontSize($fontsize2);
    $draw->setFont(realpath("./images/fonts/arialunicodems.ttf"));
    $draw->annotation(98, 2408, $text_music);
    
    $draw->setFontSize($fontsize3);
    $draw->setFont(realpath("./images/fonts/LatoRegular.ttf"));
    $draw->setFillColor('rgb(202,202,202)');
    $draw->annotation(1900, 3190, $order_id);
    $image->drawImage($draw);

    $image->setImageFormat('png8');
    $image->setImageDepth(8);
    
    if ($_SESSION['lowquality'] !== false) {
        $colors = 255;
        $image->quantizeImage($colors, Imagick::COLORSPACE_SRGB, 0, false, false);
    }
    
    $filename = realpath("./images/generated/") . "/" . "$image_name.png";
    if ($image->writeImage($filename) !== FALSE) {
        return json_encode(['status' => 200, 'orderid' => $order_id_n, 'filename' => "$image_name.png"]);
    } else {
        return json_encode(['status' => 400, 'msg' => 'File-write Error!']);
    }
}

function preview($filename = '') {
    $image = new Imagick(realpath(DIR . $filename));
    $image->thumbnailImage(2000 / ZOOM, 3200 / ZOOM);
    header('Content-type: image/png');
    echo $image->getImageBlob();
}

// Remove unneed letters that are letters within (), - xxx -.
function remove_unneed_letters($str) {
    $str = trim($str);
    $str = preg_replace('~\([^()]*\)~', '', $str);
    $str = preg_replace('~\-[^()]*\-~', '-', $str);
    $str = preg_replace('/\s+/', ' ', $str);
	return $str;
}

// Process all unique UPSELL orders
function auto_upsell() {
    $conn = new mysqli(SERVERNAME, USERNAME, PASSWORD, DBNAME);
    if ($conn->connect_error) return;
    $sql = "
        SELECT o.order_id_n
        FROM orders o
        WHERE o.SKU LIKE 'MEMOPLAYGlass2%' AND (ISNULL(o.cover_image) OR o.cover_image = '')
    ";
    $result = $conn->query($sql);
    $cnt = 0;
    if ($result->num_rows > 0) {
        while ($row = $result->fetch_assoc()) {
            $upsell_order_id_n = $row['order_id_n'];
            $sql = "
                SELECT *  
                FROM orders o 
                WHERE
                    o.email = ( SELECT email FROM orders WHERE order_id_n = '{$upsell_order_id_n}' ) 
                    AND o.order_id_n NOT LIKE '{$upsell_order_id_n}'
                    AND o.SKU LIKE 'MEMOPLAYGlass%' AND NOT o.SKU REGEXP '[2-9]'
                    AND o.order_id_n < '{$upsell_order_id_n}'
                ORDER BY o.order_id_n
            ";
            $rlt = $conn->query($sql);
            if ($rlt->num_rows > 0) {
                $original_order_id_n = $rlt->fetch_assoc();
                $original_order_id_n = $original_order_id_n['order_id_n'];
                $sql = "
                    UPDATE
                        `orders` AS `dest`,
                        (
                            SELECT * FROM `orders` WHERE `order_id_n` = '{$original_order_id_n}'
                        ) AS `src`
                    SET
                        `dest`.`titre_musique` = `src`.`titre_musique`,
                        `dest`.`phrase_personnalisee` = `src`.`phrase_personnalisee`,
                        `dest`.`cover_image` = `src`.`cover_image`,
                        `dest`.`spotify_code` = `src`.`spotify_code`
                    WHERE
                        `dest`.`order_id_n` = '{$upsell_order_id_n}'
                ";
                if ($conn->query($sql) !== false) $cnt++;
            }
        }
    }
    return $cnt;
}

